package org.tnsif.Looping;
import java.util.Scanner;
public class WhileDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s=new Scanner (System.in);
		int n = 10;
		int i = 1;
		while (i<=n)
		{
			System.out.print(i+" ");
			i++;
		}
		
		

	}

}
